mapper.select:
SELECT COUNT(*) AS cnt
FROM notice
WHERE noticeno=#{noticeno} AND passwd=#{passwd}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}, wname=#{wname}
WHERE noticeno=#{noticeno}

mapper.select:
SELECT noticeno, title, content, wname, passwd, cnt, rdate
FROM notice
WHERE noticeno=#{noticeno}
